content.Query:
let numberOfBuckets = 100;
let interval = toscalar(requests
| summarize interval = (max(timestamp)-min(timestamp)) / numberOfBuckets
| project floor(interval, 1s));
let dataset=requests
| where client_Type != "Browser"
| extend ["Operation Name"]=operation_Name;
dataset
| summarize ["Avg response time (msec)"]=avg(duration) by ["Operation Name"], bin(timestamp, interval)
| render timechart

